CREATE TABLE IF NOT EXISTS `portal_ads` (
  `ads_id` bigint(20) NOT NULL AUTO_INCREMENT,
  `ads_account_id` bigint(20) DEFAULT NULL COMMENT 'ID da conta do anunciante',
  `ads_category` varchar(50) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL COMMENT 'ID''s das categorias do anúncio',
  `ads_category_sectors` varchar(50) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL COMMENT 'Ramo de atividade ou Categoria Pai',
  `ads_title` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL COMMENT 'Título do anúncio',
  `ads_description` varchar(600) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL COMMENT 'Descrição do anúncio',
  `ads_hashtags` longtext CHARACTER SET utf8 COLLATE utf8_unicode_ci COMMENT 'Hash Tags do anúncio. Parecido com palavras-chave relacionadas',
  `ads_phone` varchar(20) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL COMMENT 'Telefone de contato',
  `ads_whatsapp` varchar(20) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL COMMENT 'Whatsapp de contato',
  `ads_email` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL COMMENT 'E-mail que irá aparecer no anúncio',
  `ads_adds` longtext CHARACTER SET utf8 COLLATE utf8_unicode_ci COMMENT 'Informações adicionais',
  `ads_opening_hours` longtext COMMENT 'Horário de funcionamento em JSON',
  `ads_type` varchar(50) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL COMMENT 'Tipo de anúncio. Se sidebar, top, destaque e etc.',
  `ads_url` varchar(355) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL COMMENT 'URL da página single do anúncio (Gera SEO)',
  `ads_cpf` varchar(15) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL COMMENT 'Mostrar CPF se necessário',
  `ads_cnpj` varchar(20) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL COMMENT 'Mostrar CNPJ se necessário',
  `ads_site` varchar(1024) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL COMMENT 'Site que irá aparecer no anúncio',
  `ads_youtube` varchar(1024) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL COMMENT 'Link do canal do youtube',
  `ads_linkedin` varchar(1024) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL COMMENT 'Link da conta do linkedin',
  `ads_facebook` varchar(1024) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL COMMENT 'Link da conta do facebook',
  `ads_instagram` varchar(1024) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL COMMENT 'Link da conta do intagram',
  `ads_tiktok` varchar(1024) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL COMMENT 'Link do tiktok',
  `ads_address` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL COMMENT 'Logradouro, Avenida ou Rua',
  `ads_address_district` varchar(100) DEFAULT NULL COMMENT 'Bairro',
  `ads_address_number` varchar(20) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL COMMENT 'Número do Endereço',
  `ads_address_complement` varchar(20) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL COMMENT 'Complemento do Endereço. Ex.: Próximo a...',
  `ads_address_state` varchar(80) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL COMMENT 'Estado',
  `ads_address_uf` varchar(3) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL COMMENT 'UF - Sigle do estado',
  `ads_address_city` varchar(200) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL COMMENT 'Cidade',
  `ads_address_zipcode` varchar(200) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL COMMENT 'CEP',
  `ads_address_view` int(1) DEFAULT '0' COMMENT 'Exibir endereço do anuncio',
  `ads_status` int(1) DEFAULT '0' COMMENT 'Verifica se o anúncio está ativo ou inativo',
  `ads_moderate` int(1) DEFAULT '0' COMMENT 'Se o anuncio foi moderado pelo super admin',
  `ads_accept_terms` int(1) DEFAULT '0' COMMENT 'Garante que o usuário tenha aceito os termos de uso do anuncio',
  `ads_registered` datetime DEFAULT CURRENT_TIMESTAMP,
  `ads_modify` datetime DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  PRIMARY KEY (`ads_id`) USING BTREE,
  KEY `fk` (`ads_account_id`) USING BTREE,
  FULLTEXT KEY `fulltext` (`ads_title`,`ads_description`,`ads_hashtags`,`ads_address`),
  CONSTRAINT `FK_portal_ads_accounts` FOREIGN KEY (`ads_account_id`) REFERENCES `accounts` (`account_id`) ON DELETE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='Responsável por manipular os anúncios em destaque do portal. Tabela do módulo ''portal''';

